import Questions from './questions.js'
import QuestionPage from './askQuestion.js'
import Model from '../models/model.js';
import React, { useState } from 'react';



let model = new Model();
class Homepage extends React.Component{
    constructor(props){
        
        super(props);
        this.state = {
            page:1,
            questions:model.getAllQstns(),
            tags:model.getAllTags(),
            active:null,
            unanswer:null
        }
        this.handleHomepage = this.handleHomepage.bind(this);
        this.handleQuestionPage = this.handleQuestionPage.bind(this);
        this.updateQuestions = this.updateQuestions.bind(this);
        this.updateTags = this.updateTags.bind(this);
        this.newest_order = this.newest_order.bind(this);
    }
    newest_order(){
        this.setState({ questions: this.state.questions.sort((a, b) => b.askDate - a.askDate)});
      }
    newest_order(){
        this.setState({ questions: this.state.questions.sort((a, b) => b.askDate - a.askDate)});
      }
    updateQuestions = (updatedQuestions) => {
        this.setState({ questions: updatedQuestions });
      }
    updateTags = (updatedTags) => {
        this.setState({ tags: updatedTags });
      }
    handleHomepage(e){
        this.setState({ page: 1});
    }

    handleQuestionPage(e) {
        this.setState({page: 2});
    }
    
    render(){
        
        
        const state = this.state.page;
        
        if (state === 1){
            
            return (
                <>
                <div className="right" id="right">
                        <Questions questions={this.state.questions}  tags = {this.state.tags}/>
                </div>
                
                </>
                
            );
        }
        else if(state === 2){
            return(
                <>
                <div className="right" id="right">
                    <QuestionPage setPage = {this.handleHomepage} updateTags={this.updateTags}updateQuestions={this.updateQuestions}/>
                </div>
                </>
            );
        }
    }   
}

export default Homepage;
