import React, { Component } from 'react';

class AskQuestionPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: '',
            detail: '',
            tags: '',
            username:''
        };
        this.handleInputChange = this.handleInputChange.bind(this);
        this.submit = this.submit.bind(this);
    }

    handleInputChange (e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    submit() {
        const title = this.state.title;
        
        if (title.length>100){
            alert("Your title is too long");
        }
        else if(title.length ==0){
            alert("Please enter the title");
        }
        else if(text.length ==0){
            alert("Please enter the text");
        }
        else if(tags_element.length ==0){
            alert("Please enter the tags");
        }
        else if(username.length ==0){
            alert("Please enter the username");
        }
    }

    render() {
        return (
            <div>
                <h id="question_title">Question Title*</h>
                <div id="title_limit">limit title to 100 characters or less</div>
                <textarea
                    id="title_input"
                    name="title"
                    value={this.state.title}
                    onChange={this.handleInputChange}
                ></textarea>

                <h id="question_title">Question Text*</h>
                <div id="title_limit">add detail</div>
                <textarea
                    id="text_input"
                    name="detail"
                    value={this.state.detail}
                    onChange={this.handleInputChange}
                ></textarea>

                <h id="question_title">Tags*</h>
                <div id="title_limit">add keywords separated by space</div>
                <textarea
                    id="tags_input"
                    name="tags"
                    value={this.state.tags}
                    onChange={this.handleInputChange}
                ></textarea>

                <h id="question_title">Username*</h>

                <textarea id="user_input"
                    name="username"
                    value={this.state.username}
                    onChange={this.handleInputChange}
                ></textarea>

                <button id="sumbit" onClick={this.submit}>
                    Post question
                </button>
                <span id="remind_sumbit"> *indicates a mandatory field</span>
            </div>
        );
    }
}

export default AskQuestionPage;
