import Questions from './questions.js'
import QuestionPage from './askQuestion.js'
import Model from '../models/model.js';
import React, { useState } from 'react';

let model = new Model();
const update_questions = model.getAllQstns();
class Homepage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            page:1
        }
        this.handleHomepage = this.handleHomepage.bind(this);
        this.handleQuestionPage = this.handleQuestionPage.bind(this);
        
    }
    const 
    handleHomepage(e){
        this.setState({page: 1});
    }
    handleQuestionPage(e) {
        this.setState({page: 2});
    }

    render(){
        
        const state = this.state.page;
        
        if (state === 1){
            return (
                <>
                <div className="right" id="right">
                    
                        <div id="post_question"></div>
                        <div className="right_homepage" id="right_homepage">
                            <h1 className="line" id="homepage_header">
                                <span id="question_header">All Questions</span>
                                <button id="ask_question" onClick={this.handleQuestionPage}>Ask Question</button>
                            </h1>
        
                            <div >
                                <p>
                                    <span id="num_question">{questions.length}</span>
                                    <span> questions</span>
                                    
                                    <table border="1">
                                        <tr>
                                            <td><button className="select_button" id="newest">Newest</button></td>
                                            <td><button className="select_button" id="active">Active</button></td>
                                            <td><button className="select_button" id="unanswered">Unanswered</button></td>
                                        </tr>
                                    </table>
                                </p>
                            </div>
        
                            <div style={{ borderBottom: "1px dotted black" }}></div>
                            <div id="questions-container"></div>
                            <div id="search_result"></div>
                        </div>
                        <Questions />
                </div>
                
                </>
                
            );
        }
        else if(state === 2){
            return(
                <>
                <div className="right" id="right">
                    <QuestionPage setPage = {this.handleHomepage}/>
                </div>
                </>
            );
        }
    }   
}

export default Homepage;

function TagsComponent( question, tags ) {
    const arr = [];
  
    for (let i = 0; i < question.tagIds.length; i++) {
      for (let j = 0; j < tags.length; j++) {
        if (question.tagIds[i] === tags[j].tid) {
          arr.push(tags[j].name);
        }
      }
    }
  
    const tagButtons = arr.map((tagName, index) => (
      <button key={index}>{tagName}</button>
    ));
    return(
      <div>
        {tagButtons}
      </div>
    )
  }
  
  function formatQuestionTime(questionDate) {
    const currentDate = new Date();
    const postDate = new Date(questionDate);
  
    const timeDifference = currentDate - postDate;
  
    // Convert milliseconds to seconds, minutes, or hours
    const secondsDifference = Math.floor(timeDifference / 1000);
    const minutesDifference = Math.floor(secondsDifference / 60);
    const hoursDifference = Math.floor(minutesDifference / 60);
  
    const monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    const month = monthNames[postDate.getMonth()];
    const date = postDate.getDate();
    const hour = postDate.getHours();
    const min = postDate.getMinutes();
    const str = `${month} ${date} at ${hour.toString().padStart(2, '0')}:${min.toString().padStart(2, '0')}`;
    const str2 = `${month} ${date}, ${postDate.getFullYear()} at ${hour.toString().padStart(2, '0')}:${min.toString().padStart(2, '0')}`;
  
    if (secondsDifference < 60) {
      return `${secondsDifference} second${secondsDifference !== 1 ? 's' : ''} ago`;
    } else if (minutesDifference < 60) {
      return `${minutesDifference} minute${minutesDifference !== 1 ? 's' : ''} ago`;
    } else if (hoursDifference < 24) {
      return `${hoursDifference} hour${hoursDifference !== 1 ? 's' : ''} ago`;
    } else if (currentDate.getFullYear() === postDate.getFullYear()) {
      return str;
    }else {
      // If more than 24 hours ago, you can display the full date or another format
      return str2;
    }
  }
