import Questions from './questions.js'
import QuestionPage from './askQuestion.js'
import QuestionButton from './askQuestionBtn.js'
import Model from '../models/model.js';
import React, { useState } from 'react';
import PostQuestion from './askQuestionBtn.js';
/*
export default function Homepage() {
    let model = new Model();
    const questions = model.getAllQstns();
    const [DisplayState, setDisplayState] = useState(1);
    
    
    if (DisplayState === 1){
        return (
            <>
            <div className="right" id="right">
                
                    <div id="post_question"></div>
                    <div className="right_homepage" id="right_homepage">
                        <h1 className="line" id="homepage_header">
                            <span id="question_header">All Questions</span>
                            <PostQuestion setPage= {setDisplayState}/>
                        </h1>
    
                        <div >
                            <p>
                                <span id="num_question">{questions.length}</span>
                                <span> questions</span>
                                
                                <table border="1">
                                    <tr>
                                        <td><button className="select_button" id="newest">Newest</button></td>
                                        <td><button className="select_button" id="active">Active</button></td>
                                        <td><button className="select_button" id="unanswered">Unanswered</button></td>
                                    </tr>
                                </table>
                            </p>
                        </div>
    
                        <div style={{ borderBottom: "1px dotted black" }}></div>
                        <div id="questions-container"></div>
                        <div id="search_result"></div>
                    </div>
                    <Questions />
            </div>
            
            </>
            
        );
    }
    else if(DisplayState === 2){
        return(
            <>
            <div className="right" id="right">
                <QuestionPage />
            </div>
            </>
        );
    }
    
    
}

