import React from "react";

class Answer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      link_index:0,
      index:0,
    }
    this.applyWebSide = this.applyWebSide.bind(this);
    //this.returnText = this.applyWebSide(this);
  }

  applyWebSide(question){
   
      console.log(question);
      const regex = /\[([^\]]+)\]\((https?:\/\/[^\s]+)\)/;
      var output = [];
      if(question.hyper == 1){
        for(let i = 0;i<question.hyperLinks.length;i++){
          const parts = question.text.split(regex);
          for(let j = 0; j < parts.length;j++){
            if(parts[j] == question.hyperText[i]){
              output.push('hyperlink**');
              i++;
              j++;
            }
            else{
              output.push(parts[j]);
            }
          }
        }
        //console.log(output);
        return output;
      }else{
        const parts = question.text.split(regex);
        return question.text;
      }
    }
  
    returnText(output,index){
      if(output!='hyperlink**'){
        return(
          <span key = {index}>
            {output}
          </span>
        );
      }
      else{
        var index = this.state.index;
        var link_index = this.state.link_index++;
        console.log(link_index)
        return(
          <a key={`a-${link_index}`} href = {this.props.questions[index].hyperLinks[link_index]}>
            {this.props.questions[index].hyperText[link_index]}
          </a>
        );
      }
      
    }
    
  
  render() {
    const questions = this.props.questions;
    const text_arr = this.applyWebSide(questions[0]);
    
    return (
      <div>
        
          {text_arr.map((text,index)=>(this.returnText(text,index)))}
        
      </div>
    );
  }

}

export default Answer;
