import React from "react";

class Answer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      questions: [],
    };
  }

  componentDidMount() {
    // Populate questions in the state, e.g., this.setState({ questions: yourQuestionsData });
  }

  applyHyperlinks(text, hyperLinks, hyperText) {
    let result = text;

    for (let i = 0; i < hyperLinks.length; i++) {
      const linkText = hyperText[i];
      const url = hyperLinks[i];

      // Replace the linkText with an anchor tag
      const anchorTag = `<a href="${url}" target="_blank">${linkText}</a>`;
      result = result.replace(`[${linkText}](${url})`, anchorTag);
    }

    return <div dangerouslySetInnerHTML={{ __html: result }} />;
  }

  render() {
    var text = 'Example Website [google web](https://www.google.com/) more text';
    const questions = this.state.questions;
    this.checkHyperLink(questions);

    return (
      <div>
        <p>
          {this.applyHyperlinks(text, questions[0].hyperLinks, questions[0].hyperText)}
        </p>
      </div>
    );
  }

  checkHyperLink(questions) {
    for (const question of questions) {
      question.hyper = 0;
      question.hyperText = [];
      question.hyperLinks = [];
    }
    const regex = /\[([^\]]+)\]\((https?:\/\/[^\s]+)\)/g;

    for (const question of questions) {
      const matches = question.text.match(regex);
      if (matches) {
        for (const match of matches) {
          question.hyper = 1;
          const [, linkText, url] = match.match(/\[([^\]]+)\]\((https?:\/\/[^\)]+)\)/);

          if (!url.startsWith('http://') && !url.startsWith('https://')) {
            alert('Hyperlink URL must start with "http://" or "https://"');
            return;
          }
          question.hyperLinks.push(url);
          question.hyperText.push(linkText);
        }
      }
    }
  }
}

export default Answer;
