import Model from '../models/model.js';


let model = new Model();

const questions = model.getAllQstns();
const tags = model.getAllTags();
const question_menu = document.getElementById("question_menu");
const questionsContainer = document.getElementById('questions-container');

export default function questions_container() {
  var numAns = question.ansIds.length;
  var views = question.views;
  var title = question.title;
  var ask = question.askedBy;
    return(
      <div className="question">
      <div className = "p1">
        <h4 className="number_answers" >${questions.ansIds.length} answers</h4>
        <h4 className = "views"> ${ questions.views} views</h4>
      </div>
  
      <div className="p2">
        <h className = "title">
          ${questions.title}
        </h>
        <br/>
        <div className="tags">
          ${tag_text}
        </div>
      </div>
      <div className="p3">
      
        <p className="ask">${questions.askedBy} asked </p>
        <p className="date"> at {date}</p>
      
      </div>

      <div></div>
   </div>
    );
}
function createQuestionElement(question) {

    
    //var date = formatQuestionTime(question.askDate);
    var date = 1;
    //[t1,t2]->{"react"}
    var arr =[];
    
    for(let i = 0; i < question.tagIds.length;i++){
      for(let j = 0; j < tags.length;j++){
        if(question.tagIds[i] == tags[j].tid){
          arr.push(tags[j].name);
        }
      }
    }
    
    let tag_text = '';
    
    for(let i = 0; i<arr.length;i++){
      tag_text+=`<button>${arr[i]}</button>`;
    }
    
    return (<div className="question">
        <div className = "p1">
          <h4 className="number_answers" >${numAns} answers</h4>
          <h4 className = "views"> ${views} views</h4>
        </div>
    
        <div className="p2">
          <h className = "title">
            ${title}
          </h>
          <br/>
          <div className="tags">
            ${tag_text}
          </div>
        </div>
        <div className="p3">
        
          <p className="ask">${ask} asked </p>
          <p className="date"> at ${date}</p>
        
        </div>

        <div></div>
     </div>
    );
  }

  // Function to display questions
  function displayQuestions(questions) {
    //initial_post_question();
    question_menu.style.backgroundColor = "lightgray";

    questionsContainer.innerHTML = '';
    
    const num_q = document.getElementById('num_question');
    num_q.innerHTML = `${questions.length} `;
    questions.forEach(question => {
      const questionElement = createQuestionElement(question);
      questionsContainer.innerHTML += questionElement;
    });
  }
