import Model from '../models/model.js';
import LeftMenu from './leftMenu.js'
import Homepage from './homepage.js'
import React, { useState } from 'react';
import App from '../App.js'
import ReactDOM from 'react-dom/client';

let model = new Model();
class FakeStackOverflow extends React.Component {
  constructor(props){
    super(props);
    this.state ={
      page:1,
      questions:model.getAllQstns().sort((a, b) => b.askDate - a.askDate),
      tags:model.getAllTags(),
      active:null,
      unanswer:[],
      searching: []
    }
    this.updateState = this.updateState.bind(this);
  }
  
  updateState(newState){
    setPage(newState);
  }
  render(){
    return (
      <>
        <div className="header">
            <h1 id="header">Fake Stack Overflow</h1>
            <input type="text" placeholder="Search ..." id="search" /> 
        </div>
        <div id="main" className="main">
          <LeftMenu  updateState = {updateState}/>
          <Homepage newpage ={page} updateState = {updateState}/>
        </div>
        
      </>
    );
  }
  
}

export default FakeStackOverflow;
